CODE.JAVASCRIPT:

function show_keystates_bottom() {
  var margin = 10;
  var height = 20;
  var width = 20;
  var right = Rho.System.screenWidth - margin;

  Rho.Keystate.showStates( {
    top : Rho.System.screenHeight - margin - height,
    right : right,
    width : width,
    height : height
  }); 
}
